// Алгоритм зміни картинки при ховері на блок
// клас sliderNone - забирає картинку
// клас sliderBlock - додає картину
// mouseenter - коли мишка наводиться
// mouseleave - коли мишка забирається
$('.stack-left-block').each(function (index) {
    $(this).on('mouseenter', () => {
        if ($('.stack-left-block').eq(index).hasClass('active')) {

        } else {
            $('.stack-slaider-container-none').removeClass('sliderBlock')
            $('.stack-left-block').removeClass('active')

            $('.stack-slaider-container-none').eq(index).addClass('sliderBlock')
            $('.stack-left-block').eq(index).addClass('active')
            $('.stack-slaider-container-block').addClass('sliderNone')
        }
    })

    $(this).on('mouseleave', () => {
        $('.stack-slaider-container-none').removeClass('sliderBlock')
        $('.stack-left-block').removeClass('active')
        $('.stack-slaider-container-block').removeClass('sliderNone')
    })
})

// --------> --------> --------> --------> --------> --------> --------> --------> --------> --------> --------> -------->

// // Активація burger-open

        // jQuery 
// header-burger-btn Коли клацнути елемент із класом , виконуються такі дії:
// Додайте клас body-not-scroll до елемента з ID body-id.
// Додайте клас burger-open до елемента за допомогою класу burger.
// Додайте клас header-close до елемента за допомогою класу header-burger
$('.header-burger-btn').on('click', function()  {
    $('#body-id').addClass('body-not-scroll');
    $('.burger').addClass('burger-open');
    $('.header-burger').addClass('header-close');
})

// Для кожного елемента з класом burger-menu-close налаштовано прослуховувач подій, щоб ініціювати такі дії, коли його клацнути:
// Видалити клас body-not-scroll з елемента з ID body-id.
// Видалити клас burger-open з елемента з класом burger.
// Видалити клас header-close з елемента з класом header-burger.
$('.burger-menu-close').each(function() {
    $(this).on('click', () => {
        $('#body-id').removeClass('body-not-scroll');
        $('.burger').removeClass('burger-open');
        $('.header-burger').removeClass('header-close');
        })
})

// burger-close Коли клацнути елемент із класом , виконуються такі дії:
// Видалити клас body-not-scroll з елемента з ID body-id.
// Видалити клас burger-open з елемента з класом burger.
// Видалити клас header-close з елемента з класом header-burger.
$('.burger-close').on('click', function() {
    $('#body-id').removeClass('body-not-scroll');
    $('.burger').removeClass('burger-open');
    $('.header-burger').removeClass('header-close');
})